mapper.select:
SELECT tl.tb_id as tbId, tl.tb_number as tbNumber, tl.sending_state as sendingState, tu.phone as phone, tu.tb_email as tbEmail, tu.nickname as nickname, tl.create_time as createTime, ts.tb_title as title, ts.tb_content as content, CASE WHEN tlf.tb_id IS NULL THEN 0 ELSE 1 END as follow FROM `tb_short_mess` tl LEFT JOIN tb_user tu ON tu.tb_id = tl.tb_user_id LEFT JOIN tb_letter_follow tlf ON tl.tb_id = tlf.tb_letter_id AND tlf.tb_admin_id = #{id} LEFT JOIN tb_send_mess ts ON ts.tb_id = tl.tb_send_mess_id WHERE tl.tb_admin_id = #{id}
AND tu.nickname like concat('%',#{nickname},'%')
ORDER BY tl.create_time DESC
LIMIT #{start}, #{size}

mapper.delete:
