mapper.select:
select

from tb_case

	
		and tb_case_type_id = #{tbCaseTypeId}
	
	
		 and DATE_SUB(CURDATE(), INTERVAL 7 DAY) <= date(time)  
	
	
		and tb_number = #{tbNumber}
		
	
	
		and tb_address like concat('%',#{tbAddress},'%') 
	
	
		and tb_size= #{tbSize}
	
	
		and tb_Star= #{tbStar}
	

order by create_time desc

select
count(tb_id)
from tb_case

	
		and tb_case_type_id = #{tbCaseTypeId}
	
	
		 and DATE_SUB(CURDATE(), INTERVAL 7 DAY) <= date(time)  
	
	
		and tb_number = #{tbNumber}
	
	
		and tb_address like concat('%',#{tbAddress},'%') 
	
	
		and tb_size= #{tbSize}
	
	
		and tb_Star= #{tbStar}
	
	
		and tb_case_save_category= #{tbCaseSaveCategory}

select

from tb_case

	
		and tb_case_type_id = #{tbCaseTypeId}
	
	
		 and DATE_SUB(CURDATE(), INTERVAL 7 DAY) <= date(time)  
	
	
		and tb_number = #{tbNumber}
	
	
		and tb_address like concat('%',#{tbAddress},'%') 
	
	
		and tb_size= #{tbSize}
	
	
		and tb_Star= #{tbStar}
	
	
		and tb_case_save_category= #{tbCaseSaveCategory}
	

order by create_time desc
LIMIT #{limitsTart},#{limitsEnd}
