mapper.select:
select

from tb_case

	
		and tb_case_type_id = #{tbCaseTypeId}
	
	
		 and DATE_SUB(CURDATE(), INTERVAL 7 DAY) <= date(time)  
	
	
		and tb_number= #{tbNumber}
	
	
		and tb_address= #{tbAddress}
	
	
		and tb_size= #{tbSize}
	
	
		and tb_Star= #{tbStar}
	

order by create_time desc
